export default function Header() {
  const classes = useStyles(); 
  const [uploadButtonState, setUploadButtonState] = useState("inherit");
  const [labelButtonState, setLabelButtonState] = useState("inherit");
  const [downloadButtonState, setDownloadButtonState] = useState("inherit");

  //function 
  // 1. clear selection of buttons
  // 2. apply selection of buttons to one clicked
  // 3. activate the components that are part of that screen
  return (
    <div className={classes.root}>
    <AppBar position="static">
      <Toolbar>
        <IconButton
          edge="start"
          className={classes.homeButton}
          color="inherit"
          aria-label="menu"
        >
          <HomeIcon />
        </IconButton>
        <Typography align="left" variant="h6" className={classes.title}>
          KantarClean
        </Typography>
        <Button color={uploadButtonState} onClick={() => {setUploadButtonState("primary")}}>Upload</Button> 
        <Button color={labelButtonState} onClick={() => {setLabelButtonState("primary")}}>Label</Button> 
        <Button color={downloadButtonState} onClick={() => {setDownloadButtonState("primary")}}>Download</Button> 
        {/* <Typography align="right" variant="h6" className={classes.title}>
